class Solution {
public:
    vector<int> nextGreaterElement(vector<int>& nums1, vector<int>& nums2) {
        //solving using maps and stack
        stack<int> st;
        unordered_map<int, int> map;

        //take the 2nd array
        //push it in stack from the last
        //we will be storing mapping of the next greater elemnt while pushing in stack
        //if there is a greater no. in stack we will map it to that
        //if not then to -1

        //eg: nums1 = [4,1,2], nums2 = [1,3,4,2]
        //when we push from 2nd array from back
        //when we push 2 we check stack is empty and so we map to -1 and push it in stack
        //for 4 we check if stack is not empty and then we compare it to the top, as top is less we pop it and now we map this also to -1
        //now for 3 we check if stack is not empty, and then as top elemnt which is 4 is larger so we map it to 4 and we push 3 in satck

        //so fillowing this we will have mapping like this
        //1->3
        //3->4
        //4->-1
        //2->-1

        //then we iterate in 1st array and return the mapping according to its value 

        for(int i=nums2.size()-1; i>=0; i--){
            while(!st.empty() && st.top() <= nums2[i]){
                st.pop();  //while pushing in stack, if the top element already in stack is less, then we pop it
            }
            if(st.empty()){
                map[nums2[i]] = -1; //if there is no larger elemnt then we map it to -1
            }else{
                map[nums2[i]] = st.top(); //if there is a larger elemt then we map it to the top whichis in stack
            }
            st.push(nums2[i]); //push it in stack
        }

        for(int i=0; i<nums1.size(); i++){
            nums1[i] = map[nums1[i]]; //now iterate through 1st array and reurn whatever is mapped from the stack
        }

        return nums1;
    }
};
