class Solution {
public:
    vector<int> nextGreaterElement(vector<int>& nums1, vector<int>& nums2) {

        vector<int> res(nums1.size(),-1);
        unordered_map<int,int> index;
        stack<int> store;

        int i=0;
        for(auto& it: nums1)
        {
            index[it]= i++;
        }

        for(auto& curr: nums2)
        {
            while( !store.empty() && curr> store.top() )
            {
                int val= store.top();
                int idx= index[val];
                store.pop();
                res[idx]=curr;
            }

            if( index.find(curr) != index.end() )
                store.push(curr);
        }   

        return res;
    }
};
