class Solution {
public:
    vector<int> nextGreaterElement(vector<int>& nums1, vector<int>& nums2) {
        int m = nums1.size();
        int n = nums2.size();

        unordered_map<int, int> um;
        for (int i = 0; i < m; i++) {
            um[nums1[i]] = i;
        }

        int i = 0;
        stack<int> stk;
        vector<int> res(m, -1);

        while (i < n) {
            int val = nums2[i];
            if (stk.empty()) {
                stk.push(val);
                i++; continue;
            }

            int x = stk.top();
            if (val > x) {
                if (um.find(x) != um.end()) {
                    res[um[x]] = val;
                }
                stk.pop();
            } else {                
                stk.push(val);
                i++;
            }
        }

        return res;
    }
};
```

