class Solution {
public:
    int findShortestSubArray(vector<int>& nums) {
        // mapping values in the frequency, first, last format, keeping track of the nost frequents
        unordered_map<int, int[3]> freqMap;
        int mostFreq = 0;
        vector<int> mostFreqs;
        for (int i = 0; i < nums.size(); i++) {
            int n = nums[i];
            if (freqMap.find(n) == end(freqMap)) {
                freqMap[n][0]++;
                freqMap[n][1] = i;
                freqMap[n][2] = i;
            }
            else {
                freqMap[n][0]++;
                freqMap[n][2] = i;
            }
            if (freqMap[n][0] == mostFreq) mostFreqs.push_back(n);
            else if (freqMap[n][0] > mostFreq) {
                mostFreq = freqMap[n][0];
                mostFreqs = {n};
            }
        }
        // finally, computing the size of the smallest subset of highly frequent numbers
        return accumulate(begin(mostFreqs), end(mostFreqs), INT_MAX, [&freqMap](int a, int b){
            return min(a, freqMap[b][2] - freqMap[b][1] + 1);
        });
    }
};`
