public class Solution
{
    public int FindShortestSubArray(int[] nums)
    {
        int degree = 0, minLength = Int32.MaxValue;

        // <Key: number, Value: indexes where number occurs>
        Dictionary<int, List<int>> occurency = new Dictionary<int, List<int>>();

        for (int i = 0; i < nums.Length; i++)
        {
            if (occurency.ContainsKey(nums[i]))
                occurency[nums[i]].Add(i); // Add the index to num[i]'s occurence indexes
            else
                occurency.Add(nums[i], new List<int> { i }); // initialize a list with the index where num[i] occurs at the first time  

            if (occurency[nums[i]].Count > degree)
            {
                degree = occurency[nums[i]].Count; // update the degree with a greater value if exist               
                minLength = i - occurency[nums[i]][0] + 1; // update the min length
            }
            else if (occurency[nums[i]].Count == degree)
            {
                minLength = Math.Min(minLength, i - occurency[nums[i]][0] + 1); // when having same degree, get the smallest length.
            }
        }

        return minLength;
    }
}

