class Solution {

    private class MetaData {
        int left;
        int right;
        int count;
        public MetaData (int l, int r, int c) {
            this.left = l;
            this.right = r;
            this.count = c;
        }
    }

    public int findShortestSubArray(int[] nums) {
        int maxFreq = 1;
        Map<Integer, MetaData> map = new HashMap<Integer, MetaData>();

        for (int i = 0; i < nums.length; ++i) {
            int n = nums[i];
            if (!map.containsKey(n)) {
                map.put(n, new MetaData(i, i, 1));
            } else {
                MetaData meta = map.get(n);
                map.put(n, new MetaData(meta.left, i, meta.count + 1));
                maxFreq = Math.max(maxFreq, meta.count + 1);
            }
        }

        int ans = Integer.MAX_VALUE;
        for (Integer key : map.keySet()) {
            MetaData meta = map.get(key);
            if (meta.count == maxFreq) {
                ans = Math.min(ans, meta.right - meta.left + 1);
            }
        }
        return ans;
    }
}
