            if(left.find(nums[i]) == left.end())    left[nums[i]] = i;
            right[nums[i]] = i;
        }
        // Find the minimum length subarray
        for(auto& p : freq)
            if(p.second == maxFreq)
                ans = min(ans, right[p.first] - left[p.first] + 1);
        return ans;
    }
};

```

