	if len(nums) == 0 {
		return 0
	}

	m := make(map[int][3]int)

	for i, num := range nums {
		value := m[num]

		if value[0] == 0 {
			value[0]++
			value[1] = i
			value[2] = -1
		} else {
			value[0]++
			value[2] = i
		}

		m[num] = value
	}

	res := 1

	value := m[nums[0]]
	frequency := value[0]

	if value[2] != -1 {
		res = value[2] - value[1] + 1
	}

	for i := 1; i < len(nums); i++ {
		triple := m[nums[i]]

		if triple[2] != -1 {
			if triple[0] > frequency {
				frequency = triple[0]
                res = triple[2] - triple[1] + 1
			} else if triple[0] == frequency {
				if triple[2]-triple[1]+1 < res {
					res = triple[2] - triple[1] + 1
				}
			}
		}
	}

	return res
}
