var findShortestSubArray = function(nums) {

    let map = new Map();

    for(let i=0;i<nums.length;i++){
        map.set(nums[i],map.get(nums[i])+1||1);
    }
	// sorting map according to the value in descending order(value is number of occurances)
    const sortedMap = new Map([...map.entries()].sort((a,b)=>b[1]-a[1]));

	/* finding the numbers which have occured maximum
	(initialized an array because there can be more than one) */
    let maxKeys=[],max=-1
    for(let [key,value] of sortedMap){
	// checking greater than or equal because consider [1,2,2,3,1]. If it was greater then only '1' would be present
        if(value>=max){
            max = value
            maxKeys.push(key)
        }
    }

    let minDistanceBetweenOccurances = nums.length;
	/* iterating the keys to find which had the mininum distance from its first occurance
	and last occurance(which basically gives the starting and ending point of subarray) */
    maxKeys.forEach((key)=>{
        const firstOccurance = nums.indexOf(key);
        const lastOccurance = nums.lastIndexOf(key);
        const distanceBetweenOccurances = lastOccurance - firstOccurance;
		// checking less than because there is only one unique key which has the least distance
        if(distance<minDistanceBetweenOccurances){
            minDistanceBetweenOccurances = distanceBetweenOccurances;
        }
    })
	// Adding 1 because we have found difference between the starting and ending index(0 indexed)
    return minDistanceBetweenOccurances+1;
};
