class Solution {
public:
    int findShortestSubArray(vector<int>& nums) {
    if(nums.size()==1){
        return 1;
    }
    if(nums.size()==2){
        if(nums[0]==nums[1]){
            return 2;
        }
        else{
            return 1;
        }
    }
    unordered_map<int,int> mp;
    int maxm=0;
    for(int i=0;i<nums.size();i++){
        mp[nums[i]]++;
        maxm=max(maxm,mp[nums[i]]);
    }
    if(maxm==1){
        return 1;
    }
    vector<int> vect;
    for(auto i:mp){
        if(i.second==maxm){
            vect.push_back(i.first);
        }
    }
    int minm=INT_MAX;
    for(int k=0;k<vect.size();k++){
        int i=0;
        int j=nums.size()-1;
        int p=-1;
        int q=-1;
        while(i<j){
            if(nums[i]!=vect[k]){
                i++;
            }
            else{
                p=i;
            }
            if(nums[j]!=vect[k]){
                j--;
            }
            else{
                q=j;
            }
            if(p>=0 && q>0 ){
                p=q-p+1;
                break;
            }
        }
        minm=min(minm,p);
    }return minm;
    }
};
