class Solution {
public:
    int findShortestSubArray(vector<int>& nums) {
        if(nums.size() <= 1) return nums.size();
        unordered_map<int, Element> hashmap;
        int max_freq = INT_MIN;
        // first round, record freq, first_pos and last_pos
        // also record the max_freq, i.e., the candidates
        for(int i = 0; i < nums.size(); ++i) {
            if(hashmap.count(nums[i]) == 0) {
                hashmap.insert(make_pair(nums[i],Element(1, i, i)));
                max_freq = max(max_freq, 1);
            }
            else {
                auto it = hashmap.find(nums[i]);
                ++(it->second.freq);
                it->second.last_pos = i;
                max_freq = max(max_freq, it->second.freq);
            }
        }
        int res = INT_MAX;
        // second round, check all candidates and find out min res
        for(auto it = hashmap.begin(); it != hashmap.end(); ++it) {
            if(it->second.freq == max_freq) {
                res = min(res, it->second.last_pos - it->second.first_pos+1);
            }
        }
        return res;
    }
private:
    struct Element {
        int freq;
        int first_pos;
        int last_pos;
        Element (int freq_, int first_pos_, int last_pos_) {
            freq = freq_;
            first_pos = first_pos_;
            last_pos = last_pos_;
        }
    };
};
