public class Solution {

    class Item
    {
        public int degree; // total length 
        public int count;  // count of same numbers
        public int start;  // start index
    }

    public int FindShortestSubArray(int[] nums) {
        int minDegree = 0;
        Dictionary<int, Item> dict = new Dictionary<int, Item>();        
        int maxCount = 0;

        for (int i = 0; i < nums.Length; i++) 
        {
            int num = nums[i];

            if (dict.ContainsKey(num))
            {
                dict[num].count++;
                dict[num].degree = i - dict[num].start + 1;                                               
            }
            else
            {
                dict[num] = new Item(){ start = i, count = 1, degree = 1 };
            }

            if (dict[num].count > maxCount || (dict[num].count == maxCount && dict[num].degree < minDegree))
            {
                maxCount = dict[num].count;
                minDegree = dict[num].degree;
            }
        }


        return minDegree;
    }
}
