            {
                dict[num].count++;
                dict[num].degree = i - dict[num].start + 1;                                               
            }
            else
            {
                dict[num] = new Item(){ start = i, count = 1, degree = 1 };
            }

            if (dict[num].count > maxCount || (dict[num].count == maxCount && dict[num].degree < minDegree))
            {
                maxCount = dict[num].count;
                minDegree = dict[num].degree;
            }
        }


        return minDegree;
    }
}
