class Solution {
public:
    int findShortestSubArray(vector<int>& nums) {
        unordered_map<int,int> mp ;
        int mxf=0 ;
        // Calculating maximum frequency in nums
        for(auto it : nums)   mp[it]++  ,  mxf = max(mxf , mp[it]) ;

        int l=0 , h=0 , n=nums.size() ;
        unordered_map<int,int> fr ;
        int ans=INT_MAX ;
        // Using sliding window for calculating minimum subarray with
        // maximum element frequency equal to max frequency(i.e. mxf)
        while(h < n)
        {
            fr[nums[h]]++ ;
            while(fr[nums[h]] == mxf)
            {
                ans = min(ans , h-l+1) ;
                fr[nums[l]]-- ;
                l++ ;
            }
            h++ ;
        }
        return ans ;
    }
};
```

