    public int findShortestSubArray(int[] nums) {
        //count the repetition of elements
        HashMap<Integer, Integer> count_map = new HashMap<>();
        //to keep the track of element when it is first appeared
        HashMap<Integer, Integer> first_seen = new HashMap<>();
        int degree = 0,res = 0;
        for(int i = 0;i < nums.length; i++){
            //put in it if the element is not present in this hashmap before
            first_seen.putIfAbsent(nums[i],i);
            count_map.put(nums[i],count_map.getOrDefault(nums[i],0)+1);
            if(count_map.get(nums[i] > degree)){
                degree = count_map.get(nums[i]);
                res = i - first_seen.get(nums[i])+1;  //this is the index at which repetition of element becomes more than degree
                //now check when that element was appeared first and subtract it from current index to get the range of minimum subarrray of that degree

            }
            else if(degree == count_map.get(nums[i])){
                res = Math.min(res,i-first_seen.get(nums[i])+1);
            }
        }
        return res;
    }
}

