var findShortestSubArray = function(nums) {
    const hashmap = {};
    let temp = {
        value: [],
        count: 0,
    }

    for (let i = 0; i < nums.length; i++) {
        const num = nums[i];
        if (!hashmap[num]) hashmap[num] = [];
        const indexOfNum = hashmap[num];
        indexOfNum.push(i);


        if (indexOfNum.length > temp.count) {
            temp = {
                value: [num],
                count: indexOfNum.length,
            }
        } else if (indexOfNum.length === temp.count) {
            temp.value.push(num);
        }
    }

    let output;
    for (let i = 0; i < temp.value.length; i++) {
        const subArray = hashmap[temp.value[i]];
        output = Math.min(output || +Infinity, subArray[subArray.length - 1] - subArray[0] + 1);
    }
    return output;
};
