class Solution {
public:
    int findShortestSubArray(vector<int>& nums) {
        unordered_map<int,int>freq;
        int maxElem = INT_MIN;
        for(int num:nums){
            freq[num]++;
            maxElem = max(maxElem,freq[num]);
        }
        int minLength = INT_MAX;
        unordered_map<int,bool>traversed;
        for(int i = 0;i<nums.size();i++){
            if(freq[nums[i]] == maxElem && !traversed[nums[i]]){
                int start = i;
                int remaining = maxElem - 1;
                int j = i+1;
                int last = start;
                while(j < nums.size() && remaining > 0){
                    if(nums[j] == nums[i]){
                        remaining--;
                        last = j;
                    }
                    j++;
                }
                minLength = min(last - start +1,minLength);
                traversed[nums[i]] = true;
            }
        }
        return minLength;
    }
};
