class Solution {
public:
    int findShortestSubArray(vector<int>& nums) {
        unordered_map<int,vector<int>> mp;
        int deg = 0;
        for(int i=0; i<nums.size(); i++) mp[nums[i]].push_back(i);
        for (auto& e : mp) deg = max(deg, (int)e.second.size());
        int len = nums.size();
        for (auto& e : mp) {
            if (e.second.size() == deg) {
                len = min(len, e.second.back() - e.second.front() + 1);
            }
        } return len;
    }
};
