class Solution {
public:
    int findShortestSubArray(vector<int>& arr) {
        int n = arr.size();
        unordered_map<int,int> mp;
        for(int i=0 ; i<n ; i++){
            mp[arr[i]]++;
        }
        int maxf=INT_MIN;
        int maxx = INT_MIN;
        for(auto it:mp){
            if(it.second>maxf){
                maxf = it.second;
                maxx = it.first;
            }
        }

        vector<int> maxelem;
        for(auto it:mp){
            if(it.second==maxf){
                maxelem.push_back(it.first);
            }
        }

        int minn = INT_MAX;
        for(int j=0 ; j<maxelem.size() ; j++){
            int start = 0 , end = n-1;
            for(int i=0 ; i<n ; i++){
                if(arr[i]==maxelem[j]){
                    end = i;
                }
            }

            for(int i=n-1 ; i>=0 ; i--){
                if(arr[i]==maxelem[j]){
                    start = i;
                }
            }
            minn = min(minn ,end-start+1 );
        }
        return minn;
    }
};
