 public int findShortestSubArray(int[] nums) {
        //calculate the degree
        HashMap<Integer, Integer> number = new HashMap<>();
        int degree = 0;
        for (int i = 0; i < nums.length; i++) {
            int count = number.getOrDefault(nums[i], 0) + 1;
            number.put(nums[i], count);
            degree = Math.max(degree, count);
        }
        int ans = Integer.MAX_VALUE;
        HashMap<Integer, Integer> occur = new HashMap<>();
        int end = 0;
		//build the first sliding window
        while (end < nums.length ) {
            int count = occur.getOrDefault(nums[end], 0);
            count++;
            occur.put(nums[end], count);
            if (count == degree) {
                break;
            }
            end++;
        }
        ans = Math.min(ans, end + 1);
        for (int i = 0; i < nums.length; i++) {
            if (occur.getOrDefault(nums[i], 0) != degree) {
                occur.put(nums[i], occur.getOrDefault(nums[i], 0) - 1);
            } else {
                occur.put(nums[i], occur.getOrDefault(nums[i], 0) - 1);
                //only one number whoes degree will be the k(which is the degree of the entire array)
                end++;
                while (end < nums.length ) {
                    int count = occur.getOrDefault(nums[end], 0) + 1;
                    occur.put(nums[end], count);
                    if (count == degree) {
                        break;
                    }
                    end++;
                }
                if (end == nums.length) {
                    // this sliding windows donesn't have the k degree,so should discard
                    break;
                }
            }
            //while the slidin window, if this number is delete, we just need to find a new number that his degree is k
            //start the slding
            ans = Math.min(ans, end - i);
        }
        return ans;
    }
