class Solution:
    def findShortestSubArray(self, nums: List[int]) -> int:
        x = dict(Counter(nums)) # counter all types of number

        All = {} 
        Max = max(x.values()) # find the The highest frequency number
        Min = float('inf') #  a very large value
        for i in x:
            if x[i] == Max:
                All[i] = []  # put the number into Dict

        for i in range(len(nums)):
            if nums[i] in All:
                All[nums[i]].append(i)   # record the position of The highest frequency number in nums

        for k in All:
            for i in range(len(All[k])-(Max-1)):  # find the min-distance each highest frequency number
                Min = min(Min,All[k][i+(Max-1)] - All[k][i] + 1)

        return Min 
