class Solution:
    def findShortestSubArray(self, nums: List[int]) -> int:
        hashtable = {}
        largestCount = 1
        for i in range(len(nums)):
            n = nums[i]
            if hashtable.get(n):
                num = hashtable.get(n)
                num.append(i)
                hashtable[n] = num
                if len(num) >= largestCount:
                    largestCount = len(num)
            else:
                hashtable[n] = [i]
        smallestLength = len(nums)
        for n in nums:
            arr = hashtable[n]
            if len(arr) == largestCount:
                smallestLength = min(smallestLength, arr[len(arr)-1] - arr[0])
        return smallestLength + 1
