class Solution {
public:
    int findShortestSubArray(vector<int>& nums) {
        unordered_map<int,vector<int>> mp; //mp[number]=<count, start index, end index>
        for(int i=0;i<nums.size();i++)
        {
            if(mp.find(nums[i])==mp.end())//if the first time
            {
                mp[nums[i]].push_back(0);//set zero, because will add 1 later
                mp[nums[i]].push_back(i);//start index
                mp[nums[i]].push_back(i);//end index
            }
            mp[nums[i]][0]++;//count+1
            mp[nums[i]][2]=i;//end index

        }
        int mmax=1;//max count

        for(unordered_map<int,vector<int>>::iterator it=mp.begin();it!=mp.end();it++)//get max count
            mmax=max(it->second[0],mmax);

        int res=INT_MAX;
        for(unordered_map<int,vector<int>>::iterator it=mp.begin();it!=mp.end();it++)//get result; because may more then one number is max count, so calculate and compair their length
        {
            if(it->second[0]==mmax)
                res=min(it->second[2]-it->second[1]+1,res);
        }     

        return res;
    }
};
