class Solution {
    public int findShortestSubArray(int[] nums) {

        HashMap<Integer, int[]> map = new HashMap<>(); 
        int maxOccr = Integer.MIN_VALUE; // maxOccr same as degree

        for(int i = 0; i < nums.length; i++) {

            // if it isnt contained in the map, add to it and the first occurence
            if(!map.containsKey(nums[i])) {
                map.put(nums[i], new int[]{i, i, 1}); //setting first occurence and number of occurence
            }
            else { // otherwise update the last occurence
                map.get(nums[i])[1]=i; // update last index
                map.get(nums[i])[2]++; // increase number of occurence
                map.put(nums[i],  map.get(nums[i])); 
            }

            maxOccr = Math.max(maxOccr, map.get(nums[i])[2]);   
        }

        // get the minimum length of two degrees
        int out = nums.length;        
        for(int x : map.keySet()) {
            if(map.get(x)[2] == maxOccr) 
                out = Math.min(out, map.get(x)[1]-map.get(x)[0]+1);
        }



        return out;
    }
}
