class Solution(object):
    def findShortestSubArray(self, nums):
        """
        :type nums: List[int]
        :rtype: int
        """
        counts = collections.Counter(nums)
        degree = 0
        valid = []

		# get degree
        for key, value in counts.items():
            if value > degree:
                degree = value

		# determine which num has that degree
        for key, value in counts.items():
            if value == degree:
                valid.append(key)

		# exhaust all valid nums
        ans = 0
        for v in valid:
            i = nums.index(v)
            count = 0 
			# increase subarray until degree is matched
            for j in range(i, len(nums)):
                if nums[j] == v:
                    count += 1
                if count == degree:
					# return smallest subarray length
                    if ans == 0 or j-i+1 < ans:
                        ans = j-i+1
                    break

        return ans

