class StartStopFreq {

    int frequency = 0;
    int start = -1;
    int end = -1;
}
class Solution {

    public int findShortestSubArray(int[] nums) {
        HashMap<Integer, StartStopFreq> hm = new HashMap<>();
        int result = nums.length;
        int degree = 0;

        for(int i = 0; i < nums.length; i++) {
            if(hm.containsKey(nums[i])) {
                hm.get(nums[i]).frequency += 1;
                hm.get(nums[i]).end = i;
            }
            else {
                StartStopFreq el = new StartStopFreq();
                el.frequency = 1;
                el.start = i;
                el.end = i;
                hm.put(nums[i], el);   
            }
            if(hm.get(nums[i]).frequency > degree) {
                degree = hm.get(nums[i]).frequency;
                result = i - hm.get(nums[i]).start + 1;
            }
            else if(hm.get(nums[i]).frequency == degree) {
                result = Math.min(result, i - hm.get(nums[i]).start + 1);
            }
        }
        return result;
    }

}

