int findShortestSubArray(vector<int>& nums) {
      unordered_map<int,int>mp;
        int i,j=0,mx=0,mn=INT_MAX;
        for(i=0;i<nums.size();i++)
        {
         mp[nums[i]]++;
         mx=max(mx,mp[nums[i]]);
        }
        if(mx==1)
            return 1;
        unordered_map<int,int>m;
        for(i=0;i<nums.size();i++)
        {
            m[nums[i]]++;
            if(m[nums[i]]==mx)
            {
                while(nums[j]!=nums[i])
                    j++;
                mn=min(mn,i-j+1);
                j=0;
            }
        }
       return mn;
    }
};

