class Solution:
    def climbStairs(self, n: int) -> int:
        '''
        Top down memoization: backtracking
        '''

        cache={}
        def helper(n):
            if n in cache: return cache[n]
            if n == 0 or n == 1: return 1
            cache[n] = helper(n-1) + helper(n-2)
            return cache[n]

