class Solution {
public:
    int step(int n,vector<int>&dp){
        if(n==0){
            return 1;
        }
        if(dp[n] != -1)return dp[n];
        if(n>=2){
            return dp[n] = step(n-1,dp)+step(n-2,dp);
        }
            return dp[n] = step(n-1,dp)+0;
    }
    int climbStairs(int n) {
        vector<int>dp(n+1,-1);
        step(n,dp);
        return dp[n];
    }
};
```

