class Solution {
public:
    void solve(int n , vector<int>v , int sum, int &count , map<vector<int> , int>&mp)
    {
        if(n==0)
        {
            count++;
            mp[v]++;
            return;
        }

        if(mp[v]!=0)
        {
            return ;
        }
        vector<int>v1=v;
        int sum1=sum+1;
        v1.push_back(1);
        int n1=n-1;
        if(mp[v1]!=0)
        {
            return ;
        }
        else
        solve(n1 , v1 ,sum1 , count , mp);

        //mp[v1]++;
        if(n>=2)
        {
            int sum2=sum+2;
            vector<int>v2=v;
            v2.push_back(2);
            int n2=n-2;
            if(mp[v2]!=0)
            {
                return ;
            }
            else
            solve(n2, v2 , sum2 , count  , mp);
            //mp[v2]++;
        }
        return ;

    }
    int climbStairs(int n) {

        int sum=0;
        map<vector<int> , int>mp;
        vector<int>inp;
        int c=0;
        solve(n , inp , sum ,c ,mp);
        return c;

    }
};


