class Solution {
    public int climbStairs(int n) {
	if(n==0){
	   return 1;
	}
    else if(n<0){
        return 0; 
    }
    int result=0;
	for(int i=1; i<=2 ; i++){
        result = result + climbStairs(n-i);
	}
    return result;
}
