class Solution {

	public boolean containsNearbyAlmostDuplicate (int[] nums, int k, int t) {

		TreeSet<Integer> set = new TreeSet <>();

		for (int i = 0; i < nums.length; i++) {
			Integer leftMax = set.floor (nums[i]);
			Integer rightMin = set.ceiling (nums[i]);
			if ((leftMax != null && (long) nums[i] - leftMax <= t) || (rightMin != null && (long) rightMin - nums[i] <= t)) {
				return true;
			}
			set.add (nums[i]);
			if (i >= k) {
				set.remove (nums[i - k]);
			}
		}

		return false;
	}
}

Time Complexity: O(N log N)
