// idea is that we can solve it using brute force which wil give O(nk) avg case bt worst case 
 // On^2  where k = n, it might give tle , so to improve we can sort the temp capy array once 
 // and then binary search for every elt in nums , if we can find an elt in temp which satifies 3 conditions 
 // 1 . abs(nums[i] - temp [i] [1] ) <= t 
 // 2.  abs ( i - temp[i][0]) <= k
 // 3 . i != temp[i][0] , so now we get two seperate loops both Onlogn 
 // and we know On + On = On , so Onlogn + Onlogn = Onlogn

class Solution {
    private static boolean binSrc(int [][] temp , int val , int i ,int k , int t){
        int low = 0 ;
        int high = temp.length - 1;
        while(low <= high){
            int mid = low + (high - low) / 2; 
            long n_dif = (long)temp[mid][1] - (long)val; // take long to prevent integer over flow
            int i_diff = temp[mid][0] - i;
            // if we find an elt satisfying the two condtions also , they are diiferent elts , ie ->  i != j 
            if( Math.abs(n_dif) <= t && Math.abs(i_diff) <= k && temp[mid][0] != i)
                return true;
            // else if abs diff b/w val and curr_mid elt > t our possible ans lies on the left part 
            else if(Math.abs(n_dif) > t)
                high = mid - 1 ;
            else  // else our possible lies on the right part 
                low = mid + 1;

        }
        return false;
    }
    public boolean containsNearbyAlmostDuplicate(int[] nums, int k, int t) {
        // temporary pair arr , tmp [i][0] = index , tmp[i][1] = its coresponding elt
        int temp [][] = new int [nums.length][2];
        for(int i = 0 ; i < nums.length ; i++){
            temp[i][0] = i; // index
            temp[i][1] = nums[i]; // elt 
        }
        // sort in  ascending order of elts 
        Arrays.sort(temp, (a,b) -> {
            return a[1] - b[1];
        });
        // for every elt in unsorted nums , binary src the sorted tmp array and check the given condition 
        // required in question
        for(int i = 0 ; i < nums.length ; i++){
            int elt = nums[i];
            if(binSrc(temp,elt,i, k,t))
                return true;
        }
        return false;
    }
}
