struct Solution {

    bool containsNearbyAlmostDuplicate(vector<int> &a, int k, int t) {

        int n = a.size();
        k = min(k, n);

        multiset<long long> s(a.begin(), a.begin() + k);

        auto x = s.begin();

        for (int j = 1, i = *x; j < k; ++j) {
            ++x;
            if (*x - i <= t) {
                return 1;
            }
            i = *x;
        }

        for (int i = k; i < n; ++i) {

            x = s.upper_bound(a[i]);

            if (x != s.end() && *x - a[i] <= t) {
                return 1;
            }
            if (x != s.begin()) {
                --x;
                if (a[i] - *x <= t) {
                    return 1;
                }
            }

            s.insert(a[i]);
            s.erase(a[i-k]);
        }

        return 0;
    }
};
```
