class Solution {

    public boolean containsNearbyAlmostDuplicate(int[] nums, int k, int t) {
        return treemapsol(nums, k, t);
    }

	   // a = nums[i]
    // b = nums[j]
    // a - b <= t or b - a <= t
    // b >= a - t || b <= t + a 
    public boolean treemapsol(int[] nums, int k , int t) {
         TreeSet<Long> map = new TreeSet<>();
         for(int i = 0; i < nums.length; i++) {
             long a = nums[i];
             long b1 = a-t;
             long b2 = t+a;

             Long v1 = map.ceiling(b1);
             Long v2 = map.floor(b2);

             if((v1 != null && v1 <= a) || (v2 != null && v2 >= a)) {
                 return true;
             }

             map.add(a);

             if(i >= k) {
                 map.remove((long)nums[i-k]);
             }
         }
        return false;
    }

    public boolean brute(int[] nums, int k, int t) {
        for(int i = 0; i < nums.length; i++) {
            long a = nums[i];
            for(int j = i+1; j < nums.length && j <= i + k; j++) {
                long b = nums[j];
                if(Math.abs(a-b) <= t) return true;
            }
        }
        return false;
    }
}
