        if (nums.length > 9999) return false;
        int size = 1;
        while (size <= k) {
            for (int i = 0; i < nums.length - size; i++) {
                if (Math.abs((long) nums[i] - (long) nums[i + size]) <= t) {
                    return true;
                }
            }
            size++;
        }
