        int n=nums.size();
        if(n==0 || t<0){
            return false;
        }

        vector<long long> v(nums.begin(),nums.end());
        set<long long> s;
        s.insert(v[n-1]);

        for(int i=n-2;i>=0;i--){
            if(i+k+1<n){
