// Common explanation of algorithm (Bucket sort + HashMap):
//1. Assume that we will divide nums range on t+1 buckets
//2. It means that Max differense between nums in one bucket is t
//3. It means that nums[i] and nums[j] which difference is at most 't' can be in the same or neighboring buckets
//4. Next in the loop we will find current BucketID = nums[i]/t+1
//5. Then chek it in map, chek neighboring buckets and compare condition about absolute difference between i and j is at most k
//6. Then delete from map buckets out of range

var containsNearbyAlmostDuplicate = function(nums, k, t) {

    if (k < 1 || t < 0) return false;

    const map = new Map();

    for (let i = 0; i < nums.length; i++) {

        let bucket = Math.floor(nums[i] / (t+1));  

        if (map.has(bucket)) return true;
        if (map.has(bucket-1) && Math.abs(nums[i]-map.get(bucket-1)) <= t) return true;
        if (map.has(bucket+1) && Math.abs(map.get(bucket+1)-nums[i]) <= t) return true;

        if (map.size >= k) {
            let outsideBucket = Math.floor((nums[i-k]) / (t+1));
            map.delete(outsideBucket);
        }

        map.set(bucket, nums[i]);
    }

    return false;
}
