        int fast = 0, slow = 0;
        for(fast = 1; fast < nums.size(); ++fast){
            while((long long)a[fast].first - a[slow].first > t) ++slow;
            for(int i = slow; i < fast; ++i){
                if(abs(a[i].second - a[fast].second) <= k) return 1;
            }
        }
        return 0;
    }
};

