func containsNearbyAlmostDuplicate(nums []int, k int, t int) bool {
	data := make(map[int]int)
	for i, num := range nums {
		bucket := helper(num, t+1)
		if _, ok := data[bucket]; ok {
			return true
		}
		if value, ok := data[bucket-1]; ok && num-value <= t {
			return true
		}
		if value, ok := data[bucket+1]; ok && value-num <= t {
			return true
		}
		data[bucket] = num
		if i >= k {
			delete(data, helper(nums[i-k], t+1))
		}
	}
	return false
}

func helper(num, w int) int {
	if num < 0 {
		return num/w - 1
	}
	return num / w
}
