class Solution {
public:
    bool containsNearbyAlmostDuplicate(vector<int>& nums, int k, int t) {
        int n=nums.size();
        if(n==0) return false; // Null vector
        pair<long long int,int> a[n]; 
        for(int i=0;i<n;++i)
        {
            a[i].first = (long long int)nums[i]; // Elements 
            a[i].second = i; // Indices
        }
        sort(a,a+n); // Sort the pair array in ascending order
        for(int i=0;i<n;++i)
        {
            long long int n1 = a[i].first; // Current element
            for(int j=i+1;j<n;++j) // Next element
            {
                long long int n2 = a[j].first; 
                long long int diff = abs(n1-n2);
                if(diff>t) // We do not need to check any further
                    break;
                if(diff<=(long)t && abs(a[i].second-a[j].second)<=k) // Checking for both conditions
                    return true; // Return true if found such pair
            }
        }
        return false; // Return false if no such pair is found
    }
};
