class Node {
    constructor (x) {
        this.val = x;
        this.left = null;
        this.right = null;
    }

    successor () {
        var node = this;
        if (node && node.right) {
            node = node.right;
            while (node.left) {
                node = node.left;
            }
        }
        return node;
    }

    predecessor () {
        var node = this;
        if (node && node.left) {
            node = node.left;
            while (node.right) {
                node = node.right;
            }
        }
        return node;
    }
}

class BST {
    constructor () {
        this.root = null;
    }

    add (root, val) {
        if (!root) {
            return new Node(val);
        }
        if (root.val < val) root.right = this.add(root.right, val);
        else root.left = this.add(root.left, val);
        return root;
    }

    search (root, val) {
        if (!root) return null;
        if (root.val === val) return root;
        if (root.val < val) return this.search(root.right, val);
        else return this.search(root.left, val);
    }

    nextBigger(root, val) {
        var nextBigger = null;
        while (root) {
            if (root.val >= val) {
                nextBigger = root;
                root = root.left;
            }
            else {
                root = root.right;
            }
        }
        return nextBigger;
    }

    prevSmaller (root, val) {
        var prevSmaller = null;
        while (root) {
            if (root.val <= val) {
                prevSmaller = root;
                root = root.right;
            }
            else {
                root = root.left;
            }
        }
        return prevSmaller;
    }

    delete (root, val) {
        if (!root) return null;
        if (root.val < val) {
            root.right = this.delete(root.right, val);
        }
        else if (root.val > val) {
            root.left = this.delete(root.left, val);
        }
        else {
            if (root.right) {
                var next = root.successor();
                root.val = next.val;
                root.right = this.delete(root.right, next.val);
            }
            else if (root.left) {
                var prev = root.predecessor();
                root.val = prev.val;
                root.left = this.delete(root.left, prev.val);
            }
            else root = null;
        }
        return root;
    }
}

/**
 * @param {number[]} nums
 * @param {number} k
 * @param {number} t
 * @return {boolean}
 */
var containsNearbyAlmostDuplicate = function(nums, k, t) {
    var bst = new BST();
    for (var i = 0; i < nums.length; i++) {
        // Get first node that is >= nums[i]
        var next = bst.nextBigger(bst.root, nums[i]);
        if (next && next.val <= nums[i] + t) return true;
        // Get last node that is <= nums[i]
        var prev = bst.prevSmaller(bst.root, nums[i]);
        if (prev && prev.val >= nums[i] - t) return true;
        bst.root = bst.add(bst.root, nums[i]);
        if (k <= i) {
            bst.root = bst.delete(bst.root, nums[i - k]);
        }
    }
    return false;
};
