func containsNearbyAlmostDuplicate(nums []int, k int, t int) bool {
    if t < 0 { return false }
    m := make(map[int]int)
    for i := 0; i < len(nums); i++ {
        key := nums[i] / max(t, 1)
        if nums[i] < 0 { key-- }
        if _, ok := m[key]; ok { return true }
        if v, ok := m[key - 1]; ok && abs(nums[i] - v) <= t { return true }
        if v, ok := m[key + 1]; ok && abs(nums[i] - v) <= t { return true }
        m[key] = nums[i]
        if i >= k { delete(m, nums[i - k] / max(t, 1)) }
    }
    return false
}

func abs(n int) int {
    if n > 0 { return n }
    return -n
}

