class BIT(object):
    def __init__(self, N):
        self.vals = [0] * (N+1)
    def add(self, idx, val):
        while idx < len(self.vals):
            self.vals[idx] += val
            idx += idx & -idx
    def query(self, idx):
        res = 0
        while idx:
            res += self.vals[idx]
            idx -= idx & -idx
        return res

class Solution(object):
    def bin_search(self, arr, val):
        if arr[0] >= val:
            return -1
        if arr[-1] < val:
            return len(arr) - 1
        l, r = 0, len(arr) - 1
        while r - l > 1:
            m = (r + l) >> 1
            if arr[m] >= val:
                r = m
            else:
                l = m
        return l

    def containsNearbyAlmostDuplicate(self, nums, k, t):
        """
        :type nums: List[int]
        :type k: int
        :type t: int
        :rtype: bool
        """

        ns = sorted(list(set(nums)))
        n2i = {ns[i]: i for i in range(len(ns))}

        off = 5
        bit = BIT(len(ns) + 15)

        N = len(nums)
        for i in range(N):
            if i - k - 1 >= 0:
                bit.add(n2i[nums[i-k-1]] + off, -1)

            n = nums[i]
            lower = self.bin_search(ns, n - t)
            upper = self.bin_search(ns, n + t + 1)
            cnt = bit.query(upper + off) - bit.query(lower + off)
            if cnt > 0:
                return True
            bit.add(n2i[n] + off, 1)
        return False
