class Solution:
	def containsNearbyAlmostDuplicate(self, nums: List[int], k: int, t: int) -> bool:
		n = len(nums)
		bucket= {}
		# We use t + 1 here to avoid dividing by 0
		w = t + 1
		for i in range(n):
			# We calculate the label of the value
			label = nums[i] // w

			if label in bucket:
				return True
			# We will check if there are value in the neighbor groups and if the distance is valid
			if label - 1 in bucket and abs(nums[i] - bucket[label-1]) <= t:
				return True
			if label + 1 in bucket and abs(nums[i] - bucket[label+1]) <= t:
				return True
			# Replce the bucket with the new value
			bucket[label] = nums[i]
			# In the bucket, we will only save the value that is in the window of size k. When i larger than k, we will delete the the values that is not in the window
			# We we delete this, we will need to add another checking step for the first if statement, if label in bucket, we need to check if the k window size constrain valid.
			if i >= k:
				del bucket[nums[i-k] // w]
		return False

