/**
 * @param {number[]} nums
 * @param {number} k
 * @param {number} t
 * @return {boolean}
 */
var containsNearbyAlmostDuplicate = function(nums, k, t) {
    if (t < 0) return false

    let getBucketId = function(i, t) {
        if (t == 0) return i
        return Math.floor( i / t )
    }

    let buckets = {}

    for (let i = 0; i < nums.length; i ++) {
        //console.log(buckets)


        let bucketId = getBucketId(nums[i], t)

        // there exists one in the bucket
        if (bucketId in buckets) return true 

        // check one before and one after
        let bucketIdBack = (bucketId - 1) + ""
        if (bucketIdBack in buckets && Math.abs(nums[i] - buckets[bucketIdBack]) <= t) return true

        let bucketIdForward = (bucketId + 1) + ""
        if (bucketIdForward in buckets && Math.abs(nums[i] - buckets[bucketIdForward]) <= t) return true

        buckets[bucketId] = nums[i]

        // if i >= k delete trailing 
        if (i >= k) {
            delete buckets[ getBucketId(nums[i - k], t) ]
        }

    }


    return false 
};
