class Solution {
    public int search(int[] nums, int target) {
        int start = 0, end = nums.length-1;
        int mid = start + (end - start)/2;
        return searchtwo(nums, target, mid, start, end);
    }

    public int searchtwo(int[] nums, int target, int mid, int start, int end) {
        while (start <= end) {
            if (nums[mid] == target) return mid;
            if (nums[mid] > target) {
                end = mid - 1;
                mid = (start + end)/2;
                return searchtwo(nums, target, mid, start, end);
            }
            else {
                start = mid + 1;
                mid = (start + end)/2;
                return searchtwo(nums, target, mid, start, end);
            }
        }
        return -1;
    }
}
