class Solution:
    def search(self, nums: List[int], target: int) -> int:

        left, right = 0, len(nums)-1

        while left <= right:

			# update index of mid point 
            mid = left + (right-left)//2

            if nums[mid] == target:

                # base case:
                # hit
                return mid

            if target > nums[mid]:

                # search target in right half
                left = mid + 1

            else:

