class Solution:
    def search(self, nums: List[int], target: int) -> int:
        left, right = 0, len(nums) - 1

        while left <= right and nums[left] <= target <= nums[right]:
            if nums[right] == nums[left]:
                m = left
            else:
                m = ((nums[right] - target) * left + (target - nums[left]) * right) // (nums[right] - nums[left])

            if nums[m] > target:
                right = m - 1
            elif nums[m] < target:
                left = m + 1
            else:
                return m

        return -1
