class Solution {
    func search(_ n: [Int], _ t: Int) -> Int {
        var lhs = 0, rhs = n.count - 1
        while lhs <= rhs {
            let mid = lhs + (rhs - lhs) / 2, nmid = n[mid]
            guard nmid != t else { return mid }
            nmid > t ? (rhs = mid - 1) : (lhs = mid + 1)
        }
        return -1
    }
}
